mapper.select:
SELECT BOARD_NUMBER, TITLE, WRITER, BOARD_LOCAL, CONT, TO_CHAR(INDATE + 9/24, 'yyyy-mm-dd hh24:mi:ss') AS INDATE
FROM CUSTOMER_BOARD
where board_number = #{board_number}

SELECT BOARD_NUMBER, TITLE, WRITER, RIDER_LOCAL, CONT, TO_CHAR(INDATE + 9/24, 'yyyy-mm-dd hh24:mi:ss') AS INDATE
FROM RIDER_BOARD
where board_number = #{board_number}

SELECT BOARD_NUMBER, TITLE, WRITER, BOARD_LOCAL, CONT, TO_CHAR(INDATE + 9/24, 'yyyy-mm-dd hh24:mi:ss') AS INDATE
FROM REVIEW_BOARD
where board_number = #{board_number}

mapper.update:
UPDATE CUSTOMER_BOARD
SET TITLE = #{title},
    CONT = #{cont}

where BOARD_NUMBER = #{board_number}

UPDATE RIDER_BOARD
    SET TITLE = #{title},
        CONT = #{cont}

    where BOARD_NUMBER = #{board_number}

UPDATE REVIEW_BOARD
SET TITLE = #{title},
   CONT = #{cont}

where BOARD_NUMBER = #{board_number}
