mapper.select:
SELECT BOARD_NUMBER, TITLE, WRITER, BOARD_LOCAL, CONT, C_START, C_END, MONEY, LUGGAGE, DELIVERY_INDATE, TO_CHAR(INDATE + 9/24, 'yyyy-mm-dd hh24:mi:ss') AS INDATE, BOARD_CHECK
FROM CUSTOMER_BOARD
where board_number = #{board_number}

SELECT BOARD_NUMBER, TITLE, WRITER, RIDER_LOCAL, CONT, R_START, R_END, MONEY, LUGGAGE, DELIVERY_INDATE, BOARD_CHECK, TO_CHAR(INDATE + 9/24, 'yyyy-mm-dd hh24:mi:ss') AS INDATE
FROM RIDER_BOARD
where board_number = #{board_number}

SELECT BOARD_NUMBER, TITLE, WRITER, BOARD_LOCAL, CONT, TO_CHAR(INDATE + 9/24, 'yyyy-mm-dd hh24:mi:ss') AS INDATE
FROM REVIEW_BOARD
where board_number = #{board_number}

mapper.update:
UPDATE REVIEW_BOARD
SET TITLE = #{title},
CONT = #{cont}

where BOARD_NUMBER = #{board_number}

UPDATE CUSTOMER_BOARD
SET BOARD_CHECK = #{board_check}
WHERE BOARD_NUMBER = #{board_number}

UPDATE RIDER_BOARD
SET BOARD_CHECK = #{board_check}
WHERE BOARD_NUMBER = #{board_number}

mapper.select:
SELECT *
FROM CUSTOMER_BOARD
WHERE WRITER = #{nickname}

SELECT *
FROM RIDER_BOARD
WHERE WRITER = #{nickname}

SELECT *
FROM REVIEW_BOARD
WHERE WRITER = #{nickname}
