mapper.select:
SELECT USERID
FROM USERS
WHERE NICKNAME = #{nickname}
AND   EMAIL    = #{email}

SELECT USERID
FROM USERS
WHERE USERID = #{userid}

mapper.update:
UPDATE USERS
SET NICKNAME=#{nickname},
    USER_LOCAL=#{user_local},
    RIDER=#{rider}
WHERE USERID=#{userid}

UPDATE USERS
SET PASSWD=#{passwd}
WHERE USERID=#{userid}

mapper.delete:
DELETE
FROM USERS
WHERE USERID=#{userid}
AND PASSWD=#{passwd}
