mapper.update:
     UPDATE  BOARD
	     SET    STEP = STEP + 1
	     WHERE  MENU_ID = #{ menu_id } 
	     AND    NREF    = #{ nref }
	     AND    STEP    > #{ step }

mapper.select:
SELECT IDX, MENU_ID, TITLE, WRITER, READCOUNT, ( SELECT COUNT(FILE_NUM) FROM FILES F WHERE B.IDX = F.IDX ) FILESCOUNT, TO_CHAR(REGDATE, 'YYYY-MM-DD HH24:MI:SS' ) REGDATE, BNUM, LVL, STEP, NREF FROM BOARD B
WHERE MENU_ID = #{menu_id}
ORDER BY NREF DESC, STEP ASC

SELECT * FROM ( SELECT ROW_NUMBER() OVER( ORDER BY NREF DESC, STEP ASC) RNUM, IDX, MENU_ID, TITLE, WRITER, READCOUNT, ( SELECT COUNT(FILE_NUM) FROM FILES F WHERE B.IDX = F.IDX ) FILESCOUNT, TO_CHAR(REGDATE, 'YYYY-MM-DD HH24:MI:SS' ) REGDATE, BNUM, LVL, STEP, NREF FROM BOARD B WHERE 1=1
AND MENU_ID = #{menu_id}
) T WHERE RNUM BETWEEN #{startnum} AND #{endnum}

mapper.update:
UPDATE   BOARD
 SET     TITLE  = #{ title },
         CONT   = #{ cont  }
 WHERE   IDX    = ${ idx   }
