mapper.select:
SELECT USERID,
PASSWD,
TO_CHAR(INDATE, 'YYYY-MM-DD HH24:MI:SS') INDATE
FROM  USERS
WHERE USERID = #{userid}
AND   PASSWD = #{passwd}

mapper.update:
UPDATE USERS
SET NICKNAME=#{nickname},
    USER_LOCAL=#{user_local},
    RIDER=#{rider}
WHERE USERID=#{userid}

mapper.delete:
DELETE
FROM USERS
WHERE USERID=#{userid}
AND PASSWD=#{passwd}
